var getArrayFrom = (function () {
    var toStr = Object.prototype.toString;
    var isCallable = function (fn) {
        return typeof fn === "function" || toStr.call(fn) === "[object Function]";
    };
    var toInteger = function (value) {
        var number = Number(value);
        if (isNaN(number)) { return 0; }
        if (number === 0 || !isFinite(number)) { return number; }
        return (number > 0 ? 1 : -1) * Math.floor(Math.abs(number));
    };
    var maxSafeInteger = Math.pow(2, 53) - 1;
    var toLength = function (value) {
        var len = toInteger(value);
        return Math.min(Math.max(len, 0), maxSafeInteger);
    };
    return function from(arrayLike/*, mapFn, thisArg */) {
        var C = this;

        var items = Object(arrayLike);

        if (arrayLike == null) {
            throw new TypeError("Array.from requires an array-like object - not null or undefined");
        }

        var mapFn = arguments.length > 1 ? arguments[1] : void undefined;
        var T;
        if (typeof mapFn !== "undefined") {
            if (!isCallable(mapFn)) {
                throw new TypeError("Array.from: when provided, the second argument must be a function");
            }
            if (arguments.length > 2) {
                T = arguments[2];
            }
        }
        var len = toLength(items.length);
        var A = isCallable(C) ? Object(new C(len)) : new Array(len);

        var k = 0;
        var kValue;
        while (k < len) {
            kValue = items[k];
            if (mapFn) {
                A[k] = typeof T === "undefined" ? mapFn(kValue, k) : mapFn.call(T, kValue, k);
            } else {
                A[k] = kValue;
            }
            k += 1;
        }
        A.length = len;
        return A;
    };
}());

function decodeBase64(str) {
    var encodeArr = getArrayFrom(str);
    var base64IndexTable = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";
    var len = encodeArr.length;
    var pad = 0;
    if (encodeArr[len - 1] === "=") {
        pad++;
        encodeArr[len - 1] = "A";
        if (encodeArr[len - 2] === "=") {
            pad++;
            encodeArr[len - 2] = "A";
        }
    }
    if ((encodeArr.length % 4 === 0)) {
        var i;
        var j;
        var result = [];
        var block = 0;
        var mark = 255;
        for (i = 0, j = 0; i < encodeArr.length; i += 4) {
            block = (base64IndexTable.indexOf(encodeArr[i]) << 18) | (base64IndexTable.indexOf(encodeArr[i + 1]) << 12) | (base64IndexTable.indexOf(encodeArr[i + 2]) << 6) | (base64IndexTable.indexOf(encodeArr[i + 3]));
            result[j++] = block >> 16;
            result[j++] = (block >> 8) & mark;
            result[j++] = block & mark;
        }
    }
    return result.slice(0, result.length - pad).map(function (charCode) {
        return String.fromCharCode(charCode);
    }).join("");
}

function unpad(str) {
    return str.slice(0, str.length - str[str.length - 1].charCodeAt());
}

