function decodeDesCBC(ciphertext) {
    var CBC_MODE = 1;
    var decrypt = false;
    var key = "79184391";
    var iv = "87968842";
    var encryptedMsg = decodeBase64(ciphertext);
    var decryptedText = des(key, encryptedMsg, decrypt, CBC_MODE, iv, false);
    return unpad(decryptedText);
}

function fckAllFunction($, needObj) {
    var scripts = $("script");
    for (var i = 0; i < scripts.length; i++) {
        try {
            var q = eval("(function(){" + $(scripts[i]).text() + "return " + needObj + "})()");
            if (q) {
                return q;
            }
        } catch (e) {
        }
    }
}

function searchParser(url, html) {
    var c = cheerio.load(html);
    var list = [];
    c("div.cp_a").filter(function (index, element) {
        return !(c(element).parents(".ibox").length > 0);
    }).each(function (i, e) {
        var bm = {};
        var a = c(e).children("a");

        bm.name = c(a[1]).attr("title");
        bm.url = buildUrl(c(a[1]).attr("href"), url);

        bm.logo = buildLogo(c(a[0]).find("img").attr("src"));
        var li = c(a[0]).find("li");
        bm.updateTime = c(li[2]).text().trim();
        bm.status = c(li[1]).text().trim() + "  " + c(li[0]).text().trim();
        bm.newSection = "";
        list.push(bm);
    });
    return JSON.stringify(list);
}
function removeLeadingSlash(url) {
    var re = /^\/+/;
    return url.replace(re, "");
}
function removeLeadingSlash2(url) {
    var re = /^\/[^/]+/;
    return removeLeadingSlash(url.replace(re, ""));
}
