    if (url.indexOf("http://") >= 0) {
        newUrl = url.replace("http://", protocol);
    } else {
        newUrl = url;
    }
    return protocol + removeLeadingSlash(newUrl);
}
function addHost(url) {
    var host = "www.dnvod.tv/";
    var newUrl;
    if (url.indexOf(host) >= 0 || url.indexOf("http:") >= 0) {
        newUrl = url;
    } else {
        newUrl = addProtocol(host + removeLeadingSlash(url));
    }
    return newUrl;
}
function buildLogo(url) {
    return addProtocol(url);
}
function buildUrl(url, iden) {
    var newUrl;
    if (iden) {
        var u = removeLeadingSlash2(url);
        var p = iden.split("/");
        p[p.length - 1] = u;
        newUrl = p.join("/");
    } else {
        newUrl = url;
    }
    return addHost(newUrl);
}
function bookParser(url, html) {
    var c = cheerio.load(html);
    var data = {};
    var div = c("div.p-r");
    var e = div.children("ul")[0];
    data.name = c(e).children("h1").text().trim();
    data.author = "";
    data.logo = buildLogo(c("div#spec-n1").children("img").attr("src").trim());
    data.updateTime = c(c(e).children("li")[6]).text().trim();
    data.intro = div.children("div.text").text().trim();

    var sec = c(".autoHide").children("li");
    data.sections = [];
    sec.each(function (i, e) {
        var bm = {
            name: c(e).find("a").text(),
            url: buildUrl(c(e).find("a").attr("href"), url)
        };
        if (c(e).children("div").attr("class").indexOf("v") >= 0) {
            bm.name += " vip";
        } else { /* 解锁vip后else去掉 */
            data.sections.push(bm);
        }
    });
    data.sections.reverse();
    return JSON.stringify(data);
}
function serialize(obj) {
    return Object.keys(obj).reduce(function (a, k) {
        a.push(k + "=" + encodeURIComponent(obj[k]));
        return a;
    }, []).join("&");
}
