function parseSectionUrl(url, html) {
    if (isSectionPage(url)) {
        print("1");
        sectionPage = url;
        var c = cheerio.load(html);
        var pc = fckAllFunction(c, "pc");
        var key = fckAllFunction(c, "gk()");
        jsonObj = pc;
        jsonObj.key = key;
        var jsonUrl = addHost(jsonObj.prefix + jsonObj.mobilePlayer.method + jsonObj.id + jsonObj.mobilePlayer.parameters);
        formData = {key: jsonObj.key};
        formData = serialize(formData) + "&player=true&noforeback=false";
        referer = url;
        return "CALL::POST::" + jsonUrl.replace("https", "http");
    } else if (isReturnJsonUrl(url)) {
        result = JSON.parse(html);
        referer = sectionPage;
        var encryptUrl = addHost(jsonObj.prefix + result.generator + "&key=" + result.key);
        return "CALL::GET::" + encryptUrl.replace("https", "http");
    } else if (isEncryptedVideoUrl(url)) {
        var videoUrl = decodeDesCBC(html);
        var resourceType = videoUrl.match(/\/(\w+):[^/]*$/)[1];
        videoFiles = [];
        videoFiles.push({
            url: videoUrl,
            type: "." + resourceType,
            mime: "video/" + resourceType,
            log: "",
            rtmpOption: {
                rtmp_swfurl: "http://www.dnvod.tv/_player/954684278/ckplayer.swf/[[DYNAM.IC]]/1",
                rtmp_pageurl: sectionPage,
                rtmp_flashver: "WIN 26,0,0,131"
            }
        });
        return "http://www.dnvod.tv/";
    } else {
        return "";
    }
}

function parseCookie(cookies) {
    var re = /([\w_.]+)=([^;\s]+)/;
    var separator = /[;\n]/;
    var result = {};
    cookies.split(separator).forEach(function (pair) {
        var c = re.exec(pair);
        if (c) {
            result[c[1]] = c[2];
        }
    });
    /* filter cookies */
    var filter = ["path", "domain", "expires"];
    for (var property in result) {
        if (result.hasOwnProperty(property)) {
            if (filter.indexOf(property.toLowerCase().trim()) >= 0) {
                delete result[property];
            }
        }
    }
    return result;
}

function cookieToString(cookies) {
    var result = [];
    var newCookie;
    if (typeof cookies === "object") {
        for (var property in cookies) {
            if (cookies.hasOwnProperty(property)) {
                result.push(property + "=" + cookies[property]);
            }
        }
    }
    newCookie = result.join(";");
    return newCookie;
}

function buildCookies(url, cookies) {
    var newCookies;
    if (isReturnJsonUrl(url) || isEncryptedVideoUrl(url)) {
        var newCookiesObj = parseCookie(cookies.replace(/(httponly|null)\s*/gi, ""));
        newCookiesObj._uid = jsonObj.key;
        newCookiesObj.dn_config = "device=desktop&player=CkPlayer&tech=HLS";
        newCookies = cookieToString(newCookiesObj);
    }
    return newCookies || "";
}

function buildSectionArgs(url) {
    if (isReturnJsonUrl(url)) {
        return formData.split("&").join(";");
    } else {
        return "";
    }
}
