function buildHeader(url) {
    var header;
    if (isReturnJsonUrl(url)) {
        header = "Content-Type: application/x-www-form-urlencoded;charset=UTF-8 $$ X-Requested-With: XMLHttpRequest $$ Accept: */*";
    } else if (isEncryptedVideoUrl(url)) {
        header = "X-Requested-With: ShockwaveFlash/26.0.0.131 $$ Accept: */*";
    } else {
        header = "Accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8";
    }
    return header;
}

